import { useState } from 'react'
import logo from '../img/RSHS_1_Logo.png'

const test = ({ isOpen, setOpen }) => {
  const [showSidebar, setShowSidebar] = useState(false);

  // Handle open/close functionality (replace with your desired logic)
  const handleToggleSidebar = () => {
    setShowSidebar(!showSidebar);
  };

  return (
    <div
      className={`fixed top-0 left-0 h-screen w-full overflow-y-auto bg-gray-800 opacity-75 transition duration-300 ease-in-out ${
        showSidebar ? 'z-50 translate-x-0' : '-translate-x-full'
      }`}
    >
      {/* Sidebar content */}
      <ul className="p-4">
        <li className="mb-2">
          <button onClick={handleToggleSidebar}>Close Sidebar</button>
        </li>
        {/* Add more sidebar items here */}
      </ul>
    </div>
  );
};

