import { useEffect, useState } from "react"
import axios from "axios";

export default function Category({category}) {
    const [data, setData] = useState([])
    useEffect(()=>{
        console.log(category);
        fetch('http://localhost:8081/' + category)
        .then(res => res.json())
        .then(data => setData(data))
        .catch(err => console.log(err));
        
    }, [])

    const ShowPDF = (pdfdestination) => {

        window.open('http://localhost:8081/uploads/' + pdfdestination, "_blank", "noreferrer");
    }

    const DownloadPDF = (pdfdestination) => {
        axios.get('http://localhost:8081/uploads/' + pdfdestination, {
            responseType: 'blob',
        })
        .then((obj) => {
            const url = URL.createObjectURL(obj.data);
            const a = document.createElement('a');
            a.href = url;
            a.download = pdfdestination;
            a.style.display = 'none';
            document.body.appendChild(a);
            a.click();
            a.remove();
            URL.revokeObjectURL(url);
        })
        .catch(err => console.error(err));
    }
    return(
        <div class="category table-striped table-responsive tw-m-auto tw-w-[90%] tw-rounded-lg">
            <table class="table table-striped tw-text-center">
                <thead class="tw-text-center">
                    <tr>
                        <th scope="col">#</th>
                        <th scope="col">Title</th>
                        <th scope="col">Category</th>
                        <th scope="col">Year Published</th>
                        <th scope="col">View File</th>
                        <th scope="col">Download File</th>
                    </tr>
                    
                </thead>

                <tbody>
                    {data.map((d, i) => (
                    <tr key={i}>
                        <th scope="row">{i+1}</th>
                        <td>{d.title}</td>
                        <td class="tw-w-[30px] tw-m-2">{d.category}</td>
                        <td class="tw-w-[30px]">{d.year}</td>
                        <td><button class="btn btn-primary" onClick={()=> ShowPDF(d.destination)}>View</button></td>
                        <td><button class="btn btn-primary" onClick={() => DownloadPDF(d.destination)}>Download</button></td>
                    </tr>

                    ))}
                </tbody>
            </table>
        </div>
    )
}
