import { useState } from 'react';
import Sidebar from "../components/sidebar";

export default function Dashboard() {

    const [isSidebarOpen, setIsSidebarOpen] = useState(false);

    const toggleSidebar = () => {
        setIsSidebarOpen(!isSidebarOpen)
    }

    return (
        <>
        <div class="tw-w-[50px] tw-ml-5 tw-mt-5 sm:tw-hidden tw-block" onClick={toggleSidebar}>
            <i class="bi bi-list">hello</i>
        </div>
        <div class="tw-flex tw-items-center tw-justify-center tw-h-screen">
            <p class="tw-text-[50px]">hello world</p>
        </div>

        <Sidebar />

        {isSidebarOpen && <Sidebar closeSidebar={toggleSidebar}/>}
        </>
    )
}
